mapper.insert:
insert into container_info(id,container_name, container_id, host_id, image_size, cpus, create_time, version, mem_size, disk_size, update_time) values
( #{item.id}, #{item.containerName, jdbcType=VARCHAR}, #{item.containerId, jdbcType=VARCHAR}, #{item.hostId, jdbcType=NUMERIC}, #{item.imageSize, jdbcType=DECIMAL}, #{item.cpus, jdbcType=DECIMAL}, #{item.createTime}, #{item.version, jdbcType=VARCHAR}, #{item.memSize, jdbcType=DECIMAL}, #{item.diskSize, jdbcType=DECIMAL}, #{item.updateTime} )
ON DUPLICATE KEY UPDATE container_name = CASE WHEN VALUES(container_name) IS NOT NULL THEN VALUES(container_name) ELSE container_name END, host_id = CASE WHEN VALUES(host_id) IS NOT NULL THEN VALUES(host_id) ELSE host_id END, image_size = CASE WHEN VALUES(image_size) IS NOT NULL THEN VALUES(image_size) ELSE image_size END, cpus = CASE WHEN VALUES(cpus) IS NOT NULL THEN VALUES(cpus) ELSE cpus END, create_time = CASE WHEN VALUES(create_time) IS NOT NULL THEN VALUES(create_time) ELSE create_time END, version = CASE WHEN VALUES(version) IS NOT NULL THEN VALUES(version) ELSE version END, mem_size = CASE WHEN VALUES(mem_size) IS NOT NULL THEN VALUES(mem_size) ELSE mem_size END, disk_size = CASE WHEN VALUES(disk_size) IS NOT NULL THEN VALUES(disk_size) ELSE disk_size END, update_time = CASE WHEN VALUES(update_time) IS NOT NULL THEN VALUES(update_time) ELSE update_time END;
